import React, { useLayoutEffect, useState, useEffect } from "react";
import { View, Image, Text, StatusBar } from "react-native";

// import Image from ;

import HeaderBackButton from "../components/HeaderBackButton";

//test
import { auth, db } from "../utils/firebase/config.js";

const NewTripScreen = ({ navigation }) => {
  //customize header
  useLayoutEffect(() => {
    navigation.setOptions({
      headerStyle: {
        backgroundColor: "white",
        elevation: 0,
      },
      visible: false,
      headerTransparent: true,
      headerTitleStyle: { color: "#D9FCFE" },
      headerLeft: () => <HeaderBackButton navigateTo="Login" NewTripScreen />,
    });
  }, [navigation]);

  const authUserId = auth?.currentUser?.uid; //get authenticated users unique id

  const setUserTripInfo = () => {
    //push user data to firestore
    db.collection("users")
      .doc(authUserId) //usiing auth users uniq id as document id in firestore
      .set({ numberOfDays: noOfDays, userBudget: userBudget })
      .catch((err) => {
        console.log(err);
      });
  };

  //states
  const [getStarted, setGetStarted] = useState(false);
  const [userBudget, setUserBudget] = useState("");
  const [noOfDays, setNoOfDays] = useState("");

  return (
    <View
      style={{
        // marginTop: Platform.OS === "android" ? 25 : 0,
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "#D9FCFE",
      }}
    >
      <StatusBar backgroundColor="#D9FCFE" style="light" />
      <Image
        source={require("../assets/Vectors/TripSetupVector.jpg")}
        style={{ height: 300, width: 300 }}
      />
      {getStarted ? (
        <KeyboardAvoidingView
          style={{
            padding: 25,
            height: 400,
            backgroundColor: "white",
            borderTopLeftRadius: 30,
            borderTopRightRadius: 30,
            justifyContent: "center",
            elevation: 7,
          }}
        >
          <TextInput
            placeholder="Enter your budget"
            value={userBudget}
            onChangeText={(text) => setUserBudget(text)}
            keyboardType="number-pad"
            autoFocus
          />
          <Text>Selct Number of Days</Text>
          <TextInput
            placeholder="Days"
            value={noOfDays}
            onChangeText={(text) => setNoOfDays(text)}
            keyboardType="number-pad"
          />

          <Button title="Submit" onPress={setUserTripInfo} />
        </KeyboardAvoidingView>
      ) : (
        <View
          style={{
            padding: 25,
            // height: 400,
            backgroundColor: "white",
            borderTopLeftRadius: 30,
            borderTopRightRadius: 30,
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Text style={{ fontSize: 32, padding: 20 }}>Get started</Text>
        </View>
      )}
    </View>
  );
};

export default NewTripScreen;
