import React, { useLayoutEffect, useState } from "react";
import { StyleSheet, Text, View, KeyboardAvoidingView } from "react-native";

import { auth, db } from "../utils/firebase/config.js";
import HeaderBackButton from "../components/HeaderBackButton";
import StyledTextInput from "../components/StyledTextInput";
import { ButtonPrimary } from "../components/ButtonPrimary";

const RegisterScreen = ({ navigation }) => {
  useLayoutEffect(() => {
    //configure header
    navigation.setOptions({
      headerStyle: {
        backgroundColor: "white",
        elevation: 0,
      },
      visible: false,
      // headerTransparent: true,
      headerTitleStyle: { color: "white" },
      headerLeft: () => <HeaderBackButton navigateTo="Login" />,
    });
  }, [navigation]);

  const registerUser = () => {
    console.log(email, password);
    //register user to FB
    auth
      .createUserWithEmailAndPassword(email, password)
      .then((authUser) => {
        //add displayName to authUser Object
        authUser.user.updateProfile({
          displayName: username,
        });
        console.log(authUser.additionalUserInfo.isNewUser);
        //move user to set new trip
        navigation.replace("NewTrip");
      })
      .catch((error) => console.log(error.message));
  };

  //states
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");

  return (
    <KeyboardAvoidingView
      behavior="padding"
      style={{
        backgroundColor: "white",
        flex: 1,
      }}
    >
      <View
        style={{
          flexDirection: "row",
          justifyContent: "center",
          paddingLeft: 20,
          paddingRight: 20,
        }}
      >
        <Text
          style={{
            color: "black",
            fontSize: 24,
            fontWeight: "bold",
            color: "#1da1f2",
            textAlign: "center",
          }}
        >
          Fill out some details to get started.
        </Text>
      </View>
      <View style={styles.formContainer}>
        <StyledTextInput
          autoFocus
          type="text"
          placeholder="Enter your Name"
          onChangeText={(text) => setUsername(text)}
        />
        <StyledTextInput
          type="email"
          placeholder="Enter your email address"
          value={email}
          onChangeText={(text) => {
            setEmail(text);
          }}
        />
        <StyledTextInput
          placeholder="Set a password"
          value={password}
          onChangeText={(text) => setPassword(text)}
          secureTextEntry
        />

        <ButtonPrimary onPress={registerUser}>
          <Text style={{ color: "white", fontSize: 15 }}>Create Account</Text>
        </ButtonPrimary>
      </View>
    </KeyboardAvoidingView>
  );
};

export default RegisterScreen;

const styles = StyleSheet.create({
  formContainer: {
    justifyContent: "center",
    alignItems: "center",
    // backgroundColor: "green",
    height: 300,
    paddingTop: 60,
  },
});
