import React, { useLayoutEffect, useState, useEffect } from "react";
import {
  Text,
  View,
  KeyboardAvoidingView,
  StyleSheet,
  StatusBar,
  Image,
} from "react-native";

import { auth } from "../utils/firebase/config.js";

import StyledTextInput from "../components/StyledTextInput";
import { ButtonPrimary, ButtonPrimaryTitle } from "../components/ButtonPrimary";
import {
  ButtonSecondary,
  ButtonSecondaryTitle,
} from "../components/ButtonSecondary";

const LoginScreen = ({ navigation }) => {
  //hide header
  useLayoutEffect(() => {
    navigation.setOptions({
      headerShown: false,
    });
  }, [navigation]);

  //sign in user
  const signIn = () => {
    auth
      .signInWithEmailAndPassword(email, password)
      .then((response) => {
        if (response.additionalUserInfo.isNewUser === true) {
          navigation.navigate("NewTrip");
        } else {
          navigation.replace("Home");
        }
      })
      .catch((err) => console.error(err));
  };

  //states
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  return (
    <KeyboardAvoidingView
      behavior="position"
      style={{ flex: 1, backgroundColor: "white" }}
    >
      <StatusBar barStyle="dark-content" backgroundColor="white" />
      <View
        style={{
          paddingTop: 150,
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Image
          source={require("./../assets/Vectors/LoginScreenVector.jpg")}
          style={{ height: 300, width: 300 }}
        />
      </View>
      <View
        style={{
          flexDirection: "row",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Text
          style={{
            color: "#1da1f2",
            fontWeight: "bold",
            fontSize: 28,
            fontFamily: "notoserif",
          }}
        >
          Hello Traveler👋
        </Text>
      </View>
      <View style={styles.formContainer}>
        <StyledTextInput
          autoFocus
          mode="outlined"
          label="Email"
          type="email"
          placeholder="Email"
          value={email}
          onChangeText={(text) => {
            setEmail(text);
          }}
        />
        <StyledTextInput
          placeholder="Password"
          secureTextEntry
          value={password}
          onChangeText={(text) => {
            setPassword(text);
          }}
        />

        <ButtonPrimary onPress={signIn}>
          <ButtonPrimaryTitle>Login</ButtonPrimaryTitle>
        </ButtonPrimary>
        <ButtonSecondary
          onPress={() => {
            navigation.navigate("Register");
          }}
        >
          <ButtonSecondaryTitle>New User? Create account</ButtonSecondaryTitle>
        </ButtonSecondary>
      </View>
    </KeyboardAvoidingView>
  );
};

export default LoginScreen;

const styles = StyleSheet.create({
  formContainer: {
    padding: 40,
    alignItems: "center",
    // backgroundColor: "green",
    justifyContent: "center",
  },
});
